import React, { useState } from 'react';
import { useEffect } from 'react';
import { axiosInstance } from '../../services/axios-http-client';
import "../styles/displayMeetingRoom.css";

const DisplayHalls = () => {
    const [allMeetingRooms, setAllMeetingRooms] = useState([]); 
    const [filteredMeetingRooms, setFilteredMeetingRooms] = useState([]);
    useEffect(()=> {
        axiosInstance.get("/viewallmeetingrooms").then((res) => {
            setAllMeetingRooms(res.data[0]);
            console.log(res.data[0]);
            setFilteredMeetingRooms(res.data[0]);
        }).catch((error) => {
            console.error("error fetching meeting room details", error);
        })
    },[])
  return (
    <div className="row mt-4">
        {filteredMeetingRooms.length > 0 ? filteredMeetingRooms.map(rooms => (
            <div key={rooms.id}>{rooms}</div>
        )) : "Nothing to display"}
      {filteredMeetingRooms.length > 0 ? filteredMeetingRooms.map(displayMeetingRoom => (
        <div key={displayMeetingRoom.id} className="col-md-4">
          <div className="card">
            <h4 className="card-title">{displayMeetingRoom.name}</h4><br />
            <div className="row">
              <div className="col-2"></div>
              <div className="col-4">
                <h6>Capacity</h6>
              </div>
              <div className="col-4">{displayMeetingRoom.capacity}</div>
              <div className="col-2"></div>
            </div>

            <div className="row">
              <div className="col-2"></div>
              <div className="col">
                <h6>Cost</h6>
              </div>
              <div className="col">{displayMeetingRoom.costOfRoom}</div>
              <div className="col-2"></div>
            </div>

            <div className="row">
              <div className="col-2"></div>
              <div className="col">
                <h6>Type</h6>
              </div>
              <div className="col">{displayMeetingRoom.type}</div>
              <div className="col-2"></div>
            </div>

            <div className="row">
              <div className="col-2"></div>
              <div className="col">
                <h6>Date</h6>
              </div>
              <div className="col">{displayMeetingRoom.date}</div>
              <div className="col-2"></div>
            </div><br />

            <div className="row">
              <div className="col-2"></div>
              {/* <div className="col">
                <button onClick={() => deleteMeetingRoomById(displayMeetingRoom.id)} className="btn btn-danger">Delete</button>
              </div>
              <div className="col">
                <button onClick={() => updateMeetingRoom(displayMeetingRoom.name)} className="btn btn-warning ml-2">Update</button>
              </div> */}
              <div className="col-2"></div>
            </div>
          </div>
        </div>
      )) : <h2>No meeting halls to display</h2>}
    </div>
  );
};

export default DisplayHalls;
