        //             }
        //         )

        const handleHallChange=(e)=>{
            setHall({hall,[e.target.name]:e.target.value});
        }


        return (
            <>
                <h3>Add new Hall:</h3>

                {
                    message && <h3 className="alert alert-success">{message}</h3>
                }
                {
                    errorMessage && <h3 className="alert alert-danger">{errorMessage}</h3>
                }


                <form onSubmit={handleSubmit}>
                    <p>
                        Name: <input type="text" name="name" value={hall.name} onChange={handleHallChange}    required pattern="[a-zA-Z ]{3,16}" title="Name should contain min 3 & max 16 chars , no digits and special chars allowed."></input>
                    </p>

                    <p>
                        Floor Number: <input type="number" name="floor number" value={hall.floorNumber} onChange={handleHallChange} required></input>
                    </p>

                    <p>
                        Capacity: <input type="number" name="capacity" value={hall.capacity} onChange={handleHallChange} min = "10" required></input>
                    </p>
                    <p>
                        Availabile Date: <input type="date" name="Date Available" value={hall.date} onChange={handleHallChange} ></input>
                    </p>
                    <p>
                        Cost:<input type="number" name="cost of hall" value={hall.cost} onChange={handleHallChange}></input>
                    </p>
                    <p>
                        Availability:<input type="boolean" name="availability of hall" value={hall.availability} onChange={handleHallChange}></input>
                    </p>
                    <p>
                        Category:<input type="text" name="category" value={hall.hallCategory} onChange={handleHallChange}></input>
                    </p>
                    <button type="submit">Add Hall</button>
                </form>
            </>
        );
            }
export default AddHalls;
