import React, { useState } from 'react';
import './styles/login.css'; // Import CSS file for styling
import { axiosInstance } from '../services/axios-http-client';

const Login = () => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [isAdmin, setIsAdmin] = useState(false);

  const handleEmailChange = (e) => setEmail(e.target.value);

  const handlePasswordChange = (e) => setPassword(e.target.value);

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await axiosInstance.post("client/login", {
        email: email,
        password: password
      });
      console.log(response.data);
      // Handle successful login here (e.g., navigate to another page, store the login token)
    } catch (error) {
      console.error("Login error", error.response ? error.response.data : "No response from server");
      // Handle error here (e.g., show error message to the user)
    }
  };
  const handleRoleChange = () => setIsAdmin(!isAdmin);

  return (
    <div className="login-container">
      <form onSubmit={handleSubmit} className="login-form">
        {!isAdmin ? <h2>User Login</h2> : <h2>Admin Login</h2>}
        <p>Please login to your account</p>
        <div className="input-group">
          <input type="email" id="email" name="email" value={email} placeholder="Email" onChange={handleEmailChange} required />
        </div>
        <div className="input-group">
          <input type="password" id="password" name="password" placeholder="Password" value={password} onChange={handlePasswordChange} required/>
        </div>
        <button type="submit">Login</button>
        <div className="bottom-text">
            {!isAdmin && <p>Don't have an account? <button onClick={() => {/* navigate to sign up */}}>Sign Up</button></p>}
            {!isAdmin && <p><button onClick={() => {/* navigate to forgot password */}}>Forgot password?</button></p>}
            <p>
              {isAdmin 
                ? <button onClick={handleRoleChange}>User Login?</button>
                : <button onClick={handleRoleChange}>Admin Login?</button>}
            </p>
        </div>
      </form>
    </div>
  );
};

export default Login;
